    setPlogging((plo) => {
      const next = new Tmapv2.LatLng(latitude, longitude);
      const pre = new Tmapv2.LatLng(firstY, firstX);
      plo.distance = (pre.distanceTo(next) * 0.0001).toFixed(1);
      plo.ploggingTime = resultTimeSet;
      if (plo.ploggingTime > 9) {
        plo.status = 1;
      } else {
        plo.status = 0;
      }
      return plo;
    });
    showModal('primary');
